mapper.update:
update  c_apply 
set 
 stats = #{stats,jdbcType=VARCHAR},
 notes = #{notes,jdbcType=VARCHAR}
	where  
		 apply_id = #{applyId,jdbcType=INTEGER}

mapper.select:
SELECT apply_id, bianhao , applyname, approve, people, thing, days, outtime, applytime, approvetime, driver, c_apply.usenature, stats, notes, c_register.car_id, carnumber, c_register.useNature, department.department_id, department_name, fatherdep, c_driver.driver_id, drviername FROM c_apply LEFT JOIN c_register ON c_apply.car_id = c_register.car_id LEFT JOIN department ON c_apply.department_id = department.department_id LEFT JOIN c_driver ON c_apply.driver_id = c_driver.driver_id WHERE 0 = 0
and department_id=#{departmentId}
order by applytime desc

SELECT apply_id, bianhao , applyname, approve, people, thing, days, outtime, applytime, approvetime, driver, c_apply.usenature, stats, notes, c_register.car_id, carnumber, department.department_id, department_name, fatherdep, c_driver.driver_id, drviername FROM c_apply LEFT JOIN c_register ON c_apply.car_id = c_register.car_id LEFT JOIN department ON c_apply.department_id = department.department_id LEFT JOIN c_driver ON c_apply.driver_id = c_driver.driver_id WHERE
and department_id=#{departmentId}
AND approve LIKE CONCAT( '%',#{approve},'%' )
